// import logo from './logo.svg';
import './App.css';
import Navbar from './components/Navbar';
import Textform from './components/Textform';


import About from './components/About';
import { useState } from 'react';
import Alert from './components/Alert';




// let name  = "<b>diwakar</b>";// not allowed it will just print it same with the tags included
function App() {

  const [mode, setMode] = useState('light'); // dark mode enable or not
  const [alert, setAlert] = useState(null);

  const showAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type
    })

    setTimeout(() => {
      setAlert(null);
    }, 1500);



  }


  const toggleMode = () => {
    if (mode === 'light') {
      setMode('dark');
      document.body.style.backgroundColor = '#042743';
      showAlert("Dark mode has enabled", "success");
      document.title="textutils-Dark Mode";

    }
    else {
      setMode('light');
      document.body.style.backgroundColor = 'white';
      showAlert("light mode has enabled", "danger");
    }
  }
  return (
    <>
      <Navbar title="Gaming_Hub" aboutText="About Us" mode={mode} toggleMode={toggleMode} />
      <Alert alert={alert} />
      <div className="container my-3">
        <Textform heading="Analyze your Text" mode={mode} showAlert={showAlert} />
      </div>
      <About />


    </>
  );
}


export default App;
